let select=function(){let e=document.querySelectorAll(".main-form-order__select__select-header"),t=document.querySelectorAll(".main-form-order__select-item");function r(){this.parentElement.classList.toggle("is-active")}function c(){let e=this.innerText,t=this.closest(".main-form-order__select");t.querySelector(".main-form-order__select-current").innerText=e,t.classList.remove("is-active")}e.forEach(e=>{e.addEventListener("click",r)}),t.forEach(e=>{e.addEventListener("click",c)})};select();